var DButtons = {
    ALLCONVOS_CLOSED_MARGIN_SMALL: "-100%"
    , SINGLECONVO_CLOSED_MARGIN_SMALL: "100%"

    , init: function () {}

    /** Slide in Single Convo Stream for Small Screens. **/
    , gotoSingleConvo: function()
    {
        if (DMenubar.mySmallMenuDisplay == DMenubar.DISPLAY_BLOCK)
        {
            DMenubar.myConvoCol.animate({"margin-left": this.ALLCONVOS_CLOSED_MARGIN_SMALL});
            DMenubar.mySingleConvoCol.animate({"margin-left": DMenubar.DEFAULT_LEFT_MARGIN});
        }
    }

    /** Slide in All Messages for Small Screens. **/
    , gotoAllConvos: function()
    {
        if (DMenubar.mySmallMenuDisplay == DMenubar.DISPLAY_BLOCK)
        {
            DMenubar.myConvoCol.animate({"margin-left": DMenubar.DEFAULT_LEFT_MARGIN});
            DMenubar.mySingleConvoCol.animate({"margin-left": this.SINGLECONVO_CLOSED_MARGIN_SMALL});
        }
    }
};

var DMenubar = {
    myNavBar: null
    , myLogoGroupCol: null
    , myConvoCol: null
    , mySingleConvoCol: null
    , mySmallMenu: null
    , mySmallMenuDisplay: ""
    , myBackConvosBtn: null
    , myBackConvosBtnDisplay: ""
    , mySingleConvoScrollArea: null
    , DISPLAY_HIDDEN: "none"
    , DISPLAY_BLOCK: "block"
    , SINGLECONVO_CLOSED_MARGIN: "41.5%"
    , SINGLECONVO_OPEN_MARGIN: "61.5%"
    , SINGLECONVO_OPEN_MARGIN_SMALL: "100%"
    , NAVBAR_CLOSED_MARGIN: "-20%"
    , DEFAULT_LEFT_MARGIN: "0px"

    , init: function ()
    {
        this.myNavBar =  $(".leftNavBar");
        this.myLogoGroupCol = $(".logoGroupCol");
        this.myConvoCol = $(".convoCol");
        this.mySingleConvoCol = $(".singleConvoCol");
        this.mySmallMenu = $("#small-menu-btn");

        this.initSidebar();
        this.toggleSidebar();

        $(window).resize(function ()
        {
            DMenubar.initSidebar();
        });
    }

    /** Reset positions based on screen sizes. **/
    , initSidebar: function()
    {
        var singleLogo = $(".singleLogo");
        var singleLogoDisplay = singleLogo.css("display");
        this.mySmallMenuDisplay = this.mySmallMenu.css("display");

        /** Check which Menu is Currently in Use to Trigger Animations and Positions. **/
        // < LG
        if (singleLogoDisplay == this.DISPLAY_HIDDEN)
        {
            this.myNavBar.css({"margin-left": this.NAVBAR_CLOSED_MARGIN});
            this.myConvoCol.css({"margin-left": this.DEFAULT_LEFT_MARGIN});

            // Medium Hamburger Menu is Out to Play.
            if (this.mySmallMenuDisplay == this.DISPLAY_HIDDEN)
            {
                this.mySingleConvoCol.css({"margin-left": this.SINGLECONVO_CLOSED_MARGIN});

            // Small Hamburger Menu is Out to Play.
            } else {
                this.mySingleConvoCol.css({"margin-left": this.SINGLECONVO_OPEN_MARGIN_SMALL});
            }

        // Back to Default.
        } else {
            this.myNavBar.css({"margin-left": this.DEFAULT_LEFT_MARGIN});
            this.mySingleConvoCol.css({"margin-left": this.DEFAULT_LEFT_MARGIN});
        }
    }

    /** Hamburger menu button click. **/
    , toggleSidebar: function()
    {
        $("#menu-toggle").click(function(e)
        {
            // Menubar Currently Open. Close it.
            if (DMenubar.myNavBar.css("margin-left") == DMenubar.DEFAULT_LEFT_MARGIN)
            {
                DMenubar.myNavBar.animate({"margin-left": DMenubar.NAVBAR_CLOSED_MARGIN});
                DMenubar.mySingleConvoCol.animate({"margin-left": DMenubar.SINGLECONVO_CLOSED_MARGIN});

                // Menubar Currently Closed. Open it.
            } else {
                DMenubar.myNavBar.animate({"margin-left": DMenubar.DEFAULT_LEFT_MARGIN});
                DMenubar.mySingleConvoCol.animate({"margin-left": DMenubar.SINGLECONVO_OPEN_MARGIN});
            }

        });
    }

}

